mapper.select:
SELECT assetsID,assetsType,name,owner,address,longitude,latitude,link,linkPhone,imageID,imageUrl,status,street,extJson,color,sourceID,fixPosition,deleted FROM assets

ORDER BY assetsID desc


        limit #{param.start:INTEGER},#{param.limit:INTEGER}


        limit #{param.limit:INTEGER}


        limit 1000

mapper.update:
update assets
updateTime=now(), name = #{pojo.name:VARCHAR}, owner = #{pojo.owner:VARCHAR}, address = #{pojo.address:VARCHAR}, longitude = #{pojo.longitude:DOUBLE}, latitude = #{pojo.latitude:DOUBLE}, link = #{pojo.link:VARCHAR}, linkPhone = #{pojo.linkPhone:VARCHAR}, imageID = #{pojo.imageID:INTEGER}, imageUrl = #{pojo.imageUrl:VARCHAR}, status = #{pojo.status:VARCHAR}, street = #{pojo.street:VARCHAR}, extJson = #{pojo.extJson:VARCHAR}, color = #{pojo.color:VARCHAR}, fixPosition = #{pojo.fixPosition:INTEGER}, deleted = #{pojo.deleted:INTEGER},
where assetsID = #{pojo.assetsID:INTEGER}

mapper.delete:
UPDATE assets
set deleted=1
WHERE assetsID = #{assetsID:INTEGER}
